import React, { useRef, useLayoutEffect, useEffect } from 'react';

const Background = ({scrollY, pathRadius}) => {
    const backgroundRef = useRef(null);
    const rocketRef = useRef(null);
    const pathRef = useRef(null);
    const circleRef = useRef(null);

    const arcRef = useRef(null);
    const textRef=useRef(null);

    useLayoutEffect(() => {
        const angle = 90;
        const circumference = 2 * Math.PI * pathRadius;
        const arcDistance = circumference * angle / 360;
        arcRef.current = arcDistance;
    }, []);

    useEffect(() => {
        if (!arcRef.current) return;

        const angle = Math.min(90 * scrollY / arcRef.current, 90)
        const xPos = pathRadius * Math.sin(angle * Math.PI / 180)
        const pageOffset = - (Math.max(0, scrollY - arcRef.current) + (pathRadius - (pathRadius * Math.cos(angle * Math.PI / 180))));



        rocketRef.current.style.transform = `translate(${xPos}px, 50%)rotate(${angle}deg)`
        textRef.current.style.transform = `translateY(${pageOffset}px)`
        pathRef.current.style.transform = `translateY(${pathRadius + pageOffset}px)`
        circleRef.current.style.r = `${backgroundRef.current.clientWidth / 2}px`;
    }, [scrollY]);

    return (
        <svg className="background" ref={backgroundRef}>
            <g ref={textRef}>
                <text
                    y="45%"
                    x="50%"
                    textAnchor="middle"
                    alignmentBaseline="central"
                    fill="white"
                    fontSize="36"
                >
                    Jonathan Carr
                </text>
                <text
                    y="50%"
                    x="50%"
                    textAnchor="middle"
                    alignmentBaseline="central"
                    fill="white"
                    fontSize="18"
                >
                    And some words about me!
                </text>
            </g>


            <rect
                className="rocket"
                width={100}
                height={25}
                fill="white"
                ref={rocketRef}
            />
            <g ref={pathRef}>
                <circle
                    cx="0"
                    cy="50%"
                    fill="none"
                    stroke="white"
                    opacity="10%"
                    strokeWidth="4"
                    ref={circleRef}
                />
                <line
                    x1="50%"
                    x2="50%"
                    y1="50%"
                    y2="5000px"
                    stroke="white"
                    opacity="10%"
                    strokeWidth="4"
                />
            </g>

        </svg>
    );
};

export default Background;
