import React, { useRef, useLayoutEffect, useEffect } from 'react';

const Background = ({scrollY}) => {
    const backgroundRef = useRef(null);
    const rocketRef = useRef(null);

    const radiusRef = useRef(null);
    const arcRef = useRef(null);
    const textRef=useRef(null);
    // console.log(scrollY);

    useLayoutEffect(() => {
        const width = backgroundRef.current.clientWidth;
        const radius = width / 2;
        radiusRef.current = radius;
        const angle = 90;
        const circumference = 2 * Math.PI * radius;
        const arcDistance = circumference * angle / 360;
        arcRef.current = arcDistance;
        console.log(arcDistance) 
    }, []);

    useEffect(() => {
        if (!arcRef.current) return;

        const angle = Math.min(90 * scrollY / arcRef.current, 90)
        const xPos = radiusRef.current * Math.sin(angle * Math.PI / 180)

        console.log(xPos)

        rocketRef.current.style.transform = `translate(${xPos}px, 50%)rotate(${angle}deg)`

        console.log(angle)
    }, [scrollY]);

    return (
        <svg className="background" ref={backgroundRef}>
            <g ref={textRef}>
                <text
                    x="50%"
                    y="25%"
                    textAnchor="middle"
                    alignmentBaseline="central"
                    fill="white"
                    fontSize="36"
                >
                    Jonathan Carr
                </text>
                <text
                    x="50%"
                    y="35%"
                    textAnchor="middle"
                    alignmentBaseline="central"
                    fill="white"
                    fontSize="18"
                >
                    And a blurb about me!
                </text>
            </g>


            <rect
                className="rocket"
                width={100}
                height={25}
                fill="white"
                ref={rocketRef}
            />
        </svg>
    );
};

export default Background;
