import React, { useRef, useLayoutEffect, useEffect } from 'react';
import Rocket from './Rocket';

const Background = ({scrollY, pathRadius, pathArcDistance, pathStart, height, projectsHeight, angle, xPos, pageOffset}) => {
    const backgroundRef = useRef(null);
    const rocketRef = useRef(null);
    const pathRef = useRef(null);
    const circleRef = useRef(null);
    const footerPathRef = useRef(null);

    const textRef=useRef(null);


    useEffect(() => {
        rocketRef.current.style.transform = `translate(${xPos}px, 50%)rotate(${90 + angle}deg)`
        pathRef.current.style.transform = `translateY(${pathRadius + pageOffset}px)`
        textRef.current.style.transform = `translateY(${pageOffset}px)`
        circleRef.current.style.r = `${pathRadius}px`;
        footerPathRef.current.style.r = `${pathRadius}px`;
    }, [scrollY, pathRadius, pathArcDistance, xPos, angle, pageOffset]);

    return (
        <svg className="background" ref={backgroundRef}>
            <g ref={textRef}>
                <text
                    y="45%"
                    x="50%"
                    textAnchor="middle"
                    alignmentBaseline="central"
                    fill="white"
                    fontSize="36"
                >
                    Jonathan Carr
                </text>
                <text
                    y="50%"
                    x="50%"
                    textAnchor="middle"
                    alignmentBaseline="central"
                    fill="white"
                    fontSize="18"
                >
                    I create websites to justify my true passion - buying web domains
                </text>
                <text
                    y={`${pathStart + pathArcDistance + projectsHeight + pathArcDistance / 3}px`}
                    x="50%"
                    textAnchor="middle"
                    alignmentBaseline="central"
                    fill="white"
                    fontSize="32"
                >
                    Get in touch!
                </text>
                <text
                    y={`${pathStart + pathArcDistance + projectsHeight + pathArcDistance / 3 + 48}px`}
                    x="50%"
                    textAnchor="middle"
                    alignmentBaseline="central"
                    fill="white"
                    fontSize="24"
                    pointerEvents="all"
                >
                    howdy@jonathan.co.nz
                </text>
            </g>

            <g ref={pathRef}>
                <defs>
                    <clipPath id="circle-masks">
                        <rect
                            x={0}
                            y={pathStart - pathRadius - 50}
                            width={pathRadius * 2}
                            height={`${pathRadius + 50}px`}
                            />
                        <rect
                            x={0}
                            y={pathStart + projectsHeight}
                            width={pathRadius * 2}
                            height={`${pathRadius +  50}px`}
                        />
                    </clipPath>
                </defs>

                <circle
                    cx="0"
                    cy={pathStart}
                    fill="none"
                    stroke="white"
                    opacity="0.05"
                    strokeWidth="50"
                    ref={circleRef}
                    clipPath="url(#circle-masks)"
                />
                <line
                    x1={pathRadius}
                    x2={pathRadius}
                    y1={`${pathStart}px`}
                    y2={`${pathStart + projectsHeight}px`}
                    stroke="white"
                    opacity="0.05"
                    strokeWidth="50"
                />
                <circle
                    cx={`${pathRadius * 2}px`}
                    cy={`${pathStart + projectsHeight}px`}
                    fill="none"
                    stroke="white"
                    opacity="0.05"
                    strokeWidth="50"
                    ref={footerPathRef}
                    clipPath="url(#circle-masks)"
                />
            </g>
            <g className="rocket" ref={rocketRef}>
                <Rocket />
            </g>
        </svg>
    );
};

export default Background;
