import React, { forwardRef } from 'react';
import { FaGithubSquare } from "react-icons/fa";

const PROJECTS = [
    {
        title: "Commute.nz",
        description: "A dashboard for exploring commuter data from the 2018 New Zealand census. (The rest of my projects are less serious, I promise)",
        githubRepo: "commute-nz",
        githubLink: "https://github.com/jonathancarr/commute-nz",
    },
    {
        title: "Sameageasmydog.com",
        description: "Ever wondered when you'll be the same age as your dog? Wonder no more!",
        githubRepo: "same-age-as-my-dog",
    },
    {
        title: "Cheds.club",
        description: "A public messenger where you can share your appreciation for your favourite cracker, and nothing else!",
        githubRepo: "cheds-club",
    },
]

const Header = forwardRef(({ height }, ref) => (
    <div className="projects" ref={ref} style={{ height }}>
        { PROJECTS.map(project => (
            <div className="project">
                <div className="project__col">
                    <h2 className="project__title">{project.title}</h2>
                    <p className="project__description">{project.description}</p>
                    <a className="project__github"><FaGithubSquare />{project.githubRepo}</a>
                </div>
                <div className="project__col">
                    {project.title}
                </div>
            </div>
        ))}
    </div>
));

export default Header;
