import React, { useRef, useLayoutEffect, useEffect } from 'react';

const describeArc = (x, y, radius, startAngle, endAngle) =>{

    var start = polarToCartesian(x, y, radius, endAngle);
    var end = polarToCartesian(x, y, radius, startAngle);

    var largeArcFlag = endAngle - startAngle <= 180 ? "0" : "1";

    var d = [
        "M", start.x, start.y, 
        "A", radius, radius, 0, largeArcFlag, 0, end.x, end.y
    ].join(" ");

    return d;       
}

const Background = ({scrollY}) => {
    const backgroundRef = useRef(null);
    const rocketRef = useRef(null);
    const pathRef = useRef(null);
    const circleRef = useRef(null);

    const radiusRef = useRef(null);
    const arcRef = useRef(null);
    const textRef=useRef(null);
    // console.log(scrollY);

    useLayoutEffect(() => {
        const width = backgroundRef.current.clientWidth;
        const radius = width / 2;
        radiusRef.current = radius;
        const angle = 90;
        const circumference = 2 * Math.PI * radius;
        const arcDistance = circumference * angle / 360;
        arcRef.current = arcDistance;
        console.log(arcDistance) 
    }, []);

    useEffect(() => {
        if (!arcRef.current) return;

        const angle = Math.min(90 * scrollY / arcRef.current, 90)
        const extraDist = Math.max(0, scrollY - arcRef.current);
        console.log(extraDist)
        const xPos = radiusRef.current * Math.sin(angle * Math.PI / 180)
        const height = backgroundRef.current.clientHeight;
        const textOffset = -(radiusRef.current - (radiusRef.current * Math.cos(angle * Math.PI / 180)))

        console.log(xPos)

        rocketRef.current.style.transform = `translate(${xPos}px, 50%)rotate(${angle}deg)`
        textRef.current.style.transform = `translateY(${textOffset - extraDist}px)`
        pathRef.current.style.transform = `translateY(${radiusRef.current + textOffset - extraDist}px)`
        circleRef.current.style.r = `${backgroundRef.current.clientWidth/2}px`;

        console.log(angle)
    }, [scrollY]);

    return (
        <svg className="background" ref={backgroundRef}>
            <g ref={textRef}>
                <text
                    y="45%"
                    x="50%"
                    textAnchor="middle"
                    alignmentBaseline="central"
                    fill="white"
                    fontSize="36"
                >
                    Jonathan Carr
                </text>
                <text
                    y="50%"
                    x="50%"
                    textAnchor="middle"
                    alignmentBaseline="central"
                    fill="white"
                    fontSize="18"
                >
                    And some words about me!
                </text>
            </g>


            <rect
                className="rocket"
                width={100}
                height={25}
                fill="white"
                ref={rocketRef}
            />

            <g ref={pathRef}>

                <circle
                    cx="0"
                    cy="50%"
                    fill="none"
                    stroke="white"
                    opacity="10%"
                    strokeWidth="4"
                    ref={circleRef}
                />
                <line
                    x1="50%"
                    x2="50%"
                    y1="50%"
                    y2="5000px"
                    stroke="white"
                    opacity="10%"
                    strokeWidth="4"
                />
            </g>

        </svg>
    );
};

export default Background;
